package com.demo1.Controllers;

import com.demo1.Models.Account;
import com.demo1.Models.Client;
import com.demo1.Models.Credit;
import com.demo1.Services.AccountService;
import com.demo1.Services.ClientService;
import com.demo1.Services.CreditService;

import java.math.BigDecimal;
import java.math.RoundingMode;
import java.time.format.DateTimeFormatter;
import java.util.List;
import java.util.Scanner;
import java.util.UUID;

public class CreditController {
    private static final Scanner scanner = new Scanner(System.in);
    private final ClientService clientService;
    private final AccountService accountService;
    private final CreditService creditService;

    public CreditController(ClientService clientService, AccountService accountService, CreditService creditService){
        this.clientService = clientService;
        this.accountService = accountService;
        this.creditService = creditService;
    }

    public void RequestCredit(){
        System.out.println("=========== Request Credit ==========");
        System.out.print("Enter client CIN: ");
        String cin = scanner.nextLine().trim().toUpperCase();
        if (cin.isEmpty()) { System.out.println("CIN is required."); return; }
        Client client = clientService.findByCin(cin);
        if (client == null) { System.out.println("Client not found for CIN: " + cin); return; }

        BigDecimal amount;
        while (true) {
            System.out.print("Credit amount: ");
            String in = scanner.nextLine().trim();
            try {
                amount = new BigDecimal(in).setScale(2, RoundingMode.HALF_UP);
                if (amount.compareTo(BigDecimal.ZERO) > 0) break;
                System.out.println("Amount must be > 0.");
            } catch (Exception e) { System.out.println("Invalid number."); }
        }

        int months;
        while (true) {
            System.out.print("Duration in months (e.g. 12): ");
            String in = scanner.nextLine().trim();
            try { months = Integer.parseInt(in); if (months >= 1) break; } catch (Exception ignored) {}
            System.out.println("Invalid duration. Must be >= 1.");
        }

        List<Account> accounts = accountService.findByClient(client);
        if (accounts == null || accounts.isEmpty()) { System.out.println("No accounts found for this client."); return; }

        // Exclure comptes CREDIT (on crédite seulement CURRENT / SAVINGS)
        List<Account> targetAccounts = accounts.stream()
                .filter(a -> a.getType() == Account.AccountType.CREDIT)
                .toList();
        if (targetAccounts.isEmpty()) { System.out.println("No eligible account (CURRENT/SAVINGS) to receive credit."); return; }

        System.out.println("Select target account to receive credit:");
        for (int i = 0; i < targetAccounts.size(); i++) {
            Account a = targetAccounts.get(i);
            System.out.printf("%d) %s | %s | Balance: %s %s%n", i + 1, a.getAccountNumber(), a.getType(), a.getBalance(), a.getCurrency() != null ? a.getCurrency().name() : "MAD");
        }
        int choice;
        while (true) {
            System.out.print("Enter choice [1-" + targetAccounts.size() + "]: ");
            String input = scanner.nextLine().trim();
            try { choice = Integer.parseInt(input); if (choice >= 1 && choice <= targetAccounts.size()) break; } catch (NumberFormatException ignored) {}
            System.out.println("Invalid choice. Try again.");
        }
        Account targetAccount = targetAccounts.get(choice - 1);

        try {
            Credit credit = creditService.createCredit(client, targetAccount, amount, Credit.CreditType.SIMPLE, months);
            System.out.println("Credit created successfully.");
            System.out.println("Credit ID: " + credit.getId());
            System.out.println("Amount: " + credit.getAmount());
            System.out.println("Fixed Interest Rate: " + credit.getInterestRate() + "%");
            System.out.println("Duration (months): " + months);
            System.out.println("Monthly Payment: " + creditService.getLastMonthlyPayment());
            if (credit.getEndDate() != null) {
                System.out.println("Planned End Date: " + credit.getEndDate().format(DateTimeFormatter.ISO_DATE));
            }
            System.out.println("Account credited with full amount.");
        } catch (Exception e) {
            System.out.println("Credit creation failed: " + e.getMessage());
        }
    }

    public void approveCredit(UUID creditId){

    }
}

package com.demo1.Controllers;

import com.demo1.Models.Account;
import com.demo1.Models.Client;
import com.demo1.Models.Credit;
import com.demo1.Services.AccountService;
import com.demo1.Services.ClientService;
import com.demo1.Services.CreditService;

import java.math.BigDecimal;
import java.math.RoundingMode;
import java.time.format.DateTimeFormatter;
import java.util.List;
import java.util.Scanner;
import java.util.UUID;

public class CreditController {
    private static final Scanner scanner = new Scanner(System.in);
    private final ClientService clientService;
    private final AccountService accountService;
    private final CreditService creditService;

    public CreditController(ClientService clientService, AccountService accountService, CreditService creditService){
        this.clientService = clientService;
        this.accountService = accountService;
        this.creditService = creditService;
    }

    public void RequestCredit(){
        System.out.println("=========== Request Credit ==========");
        System.out.print("Enter client CIN: ");
        String cin = scanner.nextLine().trim().toUpperCase();
        if (cin.isEmpty()) { System.out.println("CIN is required."); return; }
        Client client = clientService.findByCin(cin);
        if (client == null) { System.out.println("Client not found for CIN: " + cin); return; }

        BigDecimal amount;
        while (true) {
            System.out.print("Credit amount: ");
            String in = scanner.nextLine().trim();
            try {
                amount = new BigDecimal(in).setScale(2, RoundingMode.HALF_UP);
                if (amount.compareTo(BigDecimal.ZERO) > 0) break;
                System.out.println("Amount must be > 0.");
            } catch (Exception e) { System.out.println("Invalid number."); }
        }

        int months;
        while (true) {
            System.out.print("Duration in months (e.g. 12): ");
            String in = scanner.nextLine().trim();
            try { months = Integer.parseInt(in); if (months >= 1) break; } catch (Exception ignored) {}
            System.out.println("Invalid duration. Must be >= 1.");
        }

        List<Account> accounts = accountService.findByClient(client);
        if (accounts == null || accounts.isEmpty()) { System.out.println("No accounts found for this client."); return; }

        List<Account> targetAccounts = accounts.stream()
                .filter(a -> a.getType() != Account.AccountType.CREDIT)
                .toList();
        if (targetAccounts.isEmpty()) { System.out.println("No eligible account (CURRENT/SAVINGS) to receive credit."); return; }

        System.out.println("Select target account:");
        for (int i = 0; i < targetAccounts.size(); i++) {
            Account a = targetAccounts.get(i);
            System.out.printf("%d) %s | %s | Balance: %s %s%n", i + 1, a.getAccountNumber(), a.getType(), a.getBalance(), a.getCurrency() != null ? a.getCurrency().name() : "MAD");
        }
        int choice;
        while (true) {
            System.out.print("Enter choice [1-" + targetAccounts.size() + "]: ");
            String input = scanner.nextLine().trim();
            try { choice = Integer.parseInt(input); if (choice >= 1 && choice <= targetAccounts.size()) break; } catch (NumberFormatException ignored) {}
            System.out.println("Invalid choice. Try again.");
        }
        Account targetAccount = targetAccounts.get(choice - 1);

        try {
            Credit credit = creditService.createCredit(client, targetAccount, amount, Credit.CreditType.SIMPLE, months);
            System.out.println("Credit request saved (status = PENDING). Waiting manager approval.");
            System.out.println("Credit ID: " + credit.getId());
            System.out.println("Amount: " + credit.getAmount());
            System.out.println("Rate: 5% (fixed) | Duration: " + months + " months");
            System.out.println("Monthly (approx): " + creditService.getLastMonthlyPayment());
        } catch (Exception e) {
            System.out.println("Credit creation failed: " + e.getMessage());
        }
    }

    public void listPendingCredits() {
        System.out.println("=========== Pending Credits ===========");
        List<Credit> pending = creditService.listPending();
        if (pending == null || pending.isEmpty()) { System.out.println("No pending credits."); return; }
        for (Credit c : pending) {
            int months = 1;
            if (c.getStartDate() != null && c.getEndDate() != null) {
                months = Math.max(1, (int) java.time.temporal.ChronoUnit.MONTHS.between(c.getStartDate(), c.getEndDate()));
            }
            BigDecimal interest = c.getAmount().multiply(BigDecimal.valueOf(0.05)).setScale(2, RoundingMode.HALF_UP);
            BigDecimal monthly = c.getAmount().add(interest).divide(BigDecimal.valueOf(months), 2, RoundingMode.HALF_UP);
            System.out.println("ID: " + c.getId());
            System.out.println("  Amount: " + c.getAmount());
            System.out.println("  Months: " + months + " | Monthly: " + monthly);
            System.out.println("  Status: " + c.getStatus());
            System.out.println("  Account: " + (c.getAccount() != null ? c.getAccount().getAccountNumber() : "-") );
            System.out.println("-------------------------------------");
        }
    }

    public void ApproveCredits() {
        listPendingCredits();
        System.out.print("Enter Credit ID to approve (blank to cancel): ");
        String id = scanner.nextLine().trim();
        if (id.isEmpty()) { System.out.println("Cancelled."); return; }
        try {
            UUID creditId = UUID.fromString(id);
            Credit approved = creditService.approveCredit(creditId);
            if (approved != null) {
                System.out.println("Approved: account credited.");
            } else {
                System.out.println("Credit not found.");
            }
        } catch (IllegalArgumentException e) {
            System.out.println("Invalid UUID.");
        } catch (Exception e) {
            System.out.println("Approval failed: " + e.getMessage());
        }
    }
}

package com.demo1.Models;

import java.math.BigDecimal;
import java.util.UUID;

public class FeeRule {
    private UUID id;
    private String operationType;
    private String mode; // FIX or PERCENT
    private BigDecimal value; // amount or percent
    private String currency;
    private boolean active;

    public UUID getId() { return id; }
    public void setId(UUID id) { this.id = id; }
    public String getOperationType() { return operationType; }
    public void setOperationType(String operationType) { this.operationType = operationType; }
    public String getMode() { return mode; }
    public void setMode(String mode) { this.mode = mode; }
    public BigDecimal getValue() { return value; }
    public void setValue(BigDecimal value) { this.value = value; }
    public String getCurrency() { return currency; }
    public void setCurrency(String currency) { this.currency = currency; }
    public boolean isActive() { return active; }
    public void setActive(boolean active) { this.active = active; }
}

package com.demo1.Repository;

import com.demo1.Models.FeeRule;

public interface FeeRuleRepository {
    FeeRule findActiveByOperation(String operationType);
}

package com.demo1.Repository.implement;

import com.demo1.Config.DatabaseConfig;
import com.demo1.Models.FeeRule;
import com.demo1.Repository.FeeRuleRepository;

import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;

public class FeeRuleRepositoryImplement implements FeeRuleRepository {
    @Override
    public FeeRule findActiveByOperation(String operationType) {
        String sql = "SELECT id, operation_type, mode, value, currency, is_active FROM fee_rules WHERE operation_type = ? AND is_active = true LIMIT 1";
        try (Connection conn = DatabaseConfig.getConnection();
             PreparedStatement ps = conn.prepareStatement(sql)) {
            ps.setString(1, operationType);
            try (ResultSet rs = ps.executeQuery()) {
                if (rs.next()) {
                    FeeRule r = new FeeRule();
                    r.setId((java.util.UUID) rs.getObject("id"));
                    r.setOperationType(rs.getString("operation_type"));
                    r.setMode(rs.getString("mode"));
                    r.setValue(rs.getBigDecimal("value"));
                    r.setCurrency(rs.getString("currency"));
                    r.setActive(rs.getBoolean("is_active"));
                    return r;
                }
            }
        } catch (Exception e) {
            throw new RuntimeException("Error loading fee rule", e);
        }
        return null;
    }
}

package com.demo1.Services.impliment;

import com.demo1.Models.Account;
import com.demo1.Models.Client;
import com.demo1.Models.Credit;
import com.demo1.Repository.CreditRepository;
import com.demo1.Services.CreditService;

import java.math.BigDecimal;
import java.math.RoundingMode;
import java.time.LocalDate;
import java.util.UUID;

public class CreditServiceImplement implements CreditService {

    private final CreditRepository creditRepository;
    private BigDecimal lastMonthly = BigDecimal.ZERO.setScale(2, RoundingMode.HALF_UP);

    public CreditServiceImplement(CreditRepository creditRepository) {
        this.creditRepository = creditRepository;
    }

    @Override
    public Credit createCredit(Client client, Account account, BigDecimal amount, Credit.CreditType type, int months) {
        if (client == null || client.getId() == null) throw new IllegalArgumentException("Client required");
        if (account == null || account.getId() == null) throw new IllegalArgumentException("Account required");
        if (amount == null || amount.compareTo(BigDecimal.ZERO) <= 0) throw new IllegalArgumentException("Amount must be > 0");
        if (months <= 0) throw new IllegalArgumentException("Months must be >= 1");

        amount = amount.setScale(2, RoundingMode.HALF_UP);
        double fixedRate = 5.0; // Intérêt fixe global (non annuel) demandé

        // Intérêt global (5% du principal) quel que soit le nombre de mois
        BigDecimal interest = amount.multiply(BigDecimal.valueOf(fixedRate)).divide(BigDecimal.valueOf(100), 2, RoundingMode.HALF_UP);
        BigDecimal totalRepay = amount.add(interest).setScale(2, RoundingMode.HALF_UP);
        BigDecimal monthly = totalRepay.divide(BigDecimal.valueOf(months), 2, RoundingMode.HALF_UP);
        lastMonthly = monthly;

        // Seuils: <=50% OK (insertion PENDING), >50% rejet simple, >80% rejet fort
        if (client.getSalary() != null) {
            BigDecimal salary = client.getSalary().setScale(2, RoundingMode.HALF_UP);
            BigDecimal fifty = salary.multiply(BigDecimal.valueOf(0.50)).setScale(2, RoundingMode.HALF_UP);
            BigDecimal eighty = salary.multiply(BigDecimal.valueOf(0.80)).setScale(2, RoundingMode.HALF_UP);

            if (monthly.compareTo(eighty) > 0) {
                throw new IllegalArgumentException("Monthly payment exceeds 80% of salary: credit impossible.");
            }
            if (monthly.compareTo(fifty) > 0) {
                throw new IllegalArgumentException("Monthly payment exceeds 50% of salary for chosen duration. Adjust amount or months.");
            }
        }

        Credit credit = new Credit();
        credit.setAmount(amount);
        credit.setCurrency(Credit.Currency.MAD);
        credit.setInterestRate(fixedRate); // stock du pourcentage global appliqué
        credit.setCreditType(Credit.CreditType.SIMPLE);
        credit.setStatus(Credit.CreditStatus.PENDING); // en attente d'approbation manager
        credit.setStartDate(LocalDate.now());
        credit.setEndDate(LocalDate.now().plusMonths(months));
        credit.setAccount(account);

        return creditRepository.insertCredit(credit, account);
    }

    public Credit approveCredit(UUID creditId){
        return creditRepository.approveCredit(creditId);
    }

    @Override
    public BigDecimal getLastMonthlyPayment() { return lastMonthly; }
}

package com.demo1.Services.impliment;

import com.demo1.Models.Account;
import com.demo1.Models.Client;
import com.demo1.Models.Credit;
import com.demo1.Repository.CreditRepository;
